document.addEventListener("DOMContentLoaded",()=>{const t=window.innerWidth||document.documentElement.clientWidth||document.body.clientWidth;if(document.querySelector(".header")){const o=document.querySelector(".header");var e=e=>{200<window.scrollY?o.classList.add("scrolled"):o.classList.remove("scrolled"),t<992&&setTimeout(()=>o.querySelector(".menu").style="transition: padding .5s ease")};e(),window.addEventListener("scroll",e)}if(document.querySelector(".hamburger")){const s=document.querySelector(".hamburger"),n=document.querySelector(".header");s.addEventListener("click",()=>{document.body.classList.toggle("no-scroll"),s.classList.toggle("opened"),s.classList.contains("opened")?(n.classList.add("opening"),n.classList.add("is-opened")):(n.classList.remove("is-opened"),setTimeout(()=>n.classList.remove("opening"),300))})}});