document.addEventListener("DOMContentLoaded",()=>{const t=window.innerWidth||document.documentElement.clientWidth||document.body.clientWidth;if(document.querySelector(".header")){const o=document.querySelector(".header");var e=e=>{200<window.scrollY?o.classList.add("scrolled"):o.classList.remove("scrolled"),t<992&&setTimeout(()=>o.querySelector(".menu").style="transition: padding .5s ease")};e(),window.addEventListener("scroll",e)}if(document.querySelector(".hamburger")){const n=document.querySelector(".hamburger"),s=document.querySelector(".header");n.addEventListener("click",()=>{document.body.classList.toggle("no-scroll"),n.classList.toggle("opened"),n.classList.contains("opened")?(s.classList.add("opening"),s.classList.add("is-opened")):(s.classList.remove("is-opened"),setTimeout(()=>s.classList.remove("opening"),300))})}if(document.querySelector(".news-list")){const c=document.querySelector(".news-list"),d=document.querySelector(".btn.show-more");d.addEventListener("click",()=>{const e=document.querySelector(".clone-list");var t=c.cloneNode(!0);e.appendChild(t)})}});